
public class Arithmatic_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int a=10;
	    int b=16;                     //Assignment Operators
	   
	 
	    System.out.println(a+b);
	    System.out.println(a-b);
	    System.out.println(a/b);
	    System.out.println(a*b);
	    System.out.println(a%b);    //Arithmatic Operators
	    System.out.println(++a);              
	    System.out.println(--b);    //Unary Operators
	    System.out.println(a>=b);
	    System.out.println(a<=b);
	    System.out.println(a>b);
	    System.out.println(a<b);    //Comparision operators
	    System.out.println(b>>a);
	    System.out.println(a<<b);   //shifted operators
	     
	    int x=30;
	    int y=40;
	  int z=(x>y)?(x+y):(y-x);      //Ternary Operators: this is like as if else statement
	    System.out.println(z);
	    
	    

	}

}
