
public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value=73878;                 //Type casting -Automatically - converting a smaller type to a larger type size
		long value1=value;
		System.out.println(value1);
		
		double value2=4646.54654d;      //Manually - converting a larger  type to a smaller type size
		float value3=(float)value2;
		
		System.out.println(value3);
				

	}

}
